add_action('plugins_loaded', 'woocommerce_electrapay_init', 0);

function woocommerce_electrapay_init() {

    if ( !class_exists( 'WC_Payment_Gateway' ) ) return;

    /**
     * Localisation
     */
    load_plugin_textdomain('wc-electrapay', false, dirname( plugin_basename( __FILE__ ) ) . '/languages');

    if($_GET['spmsg']!=''){
        add_action('the_content', 'showMessage_electrapay');
    }

    function showMessage_electrapay($content){
        return '<ul class="woocommerce-error"><li>'.htmlentities(urldecode($_GET['spmsg'])).'</ul>'.$content;
    }
    /**
     * Gateway class
     */
    class WC_Electrapay extends WC_Payment_Gateway {
        protected $spmsg = array();
        public function __construct(){

            $this -> id = 'electrapay';
            $this -> method_title = __('ElectraPay', 'electrapay');
            $this -> icon = 'data:image/png;base64,';
            $this -> has_fields = false;
            $this -> init_form_fields();
            $this -> init_settings();
            $this -> title = $this -> settings['title'];
            $this -> method_description = sprintf( __( 'Manage the settings of your ElectraPay Payment Gateway.', 'electrapay') );
            $this -> description = $this -> settings['description'];
            $this -> accountid = $this -> settings['accountid'];
            $this -> apikey = $this -> settings['apikey'];
            $this -> elcurrency = get_woocommerce_currency();
            $this -> paymentcategory = '';
            $this -> apibaseurl = $this -> settings['apibaseurl'];
            $this -> statusupdate = $this -> settings['statusupdate'];
            $this -> spmsg['message'] = "";
            $this -> spmsg['class'] = "";

            add_action('valid-electrapay-request', array(&$this, 'successful_request'));

            if ( version_compare( WOOCOMMERCE_VERSION, '3.0.0', '>=' ) ) {
                add_action( 'woocommerce_update_options_payment_gateways_' . $this->id, array( &$this, 'process_admin_options' ) );
            } else {
                add_action( 'woocommerce_update_options_payment_gateways', array( &$this, 'process_admin_options' ) );
            }


            add_action( 'woocommerce_receipt_' . $this->id, array( $this, 'receipt_page_electrapay' ) );
            add_action( 'woocommerce_api_' . strtolower( get_class( $this ) ), array( $this, 'check_electrapay_response' ) );

        }

        function init_form_fields(){
            //$elcurrencylist = array('ECA'=>'ECA', 'USD' => 'USD');
            //$paymentcategorylist = array('Donation'=>'Donation', 'Goods' => 'Goods');
            $pstlist =  array('C'=>'Call Back', 'Q' => 'Query');
            $this -> form_fields = array(
                'enabled' => array(
                    'title' => __('Enable/Disable', 'electrapay'),
                    'type' => 'checkbox',
                    'label' => __('Enable ElectraPay Payment Module.', 'electrapay'),
                    'default' => 'no'),
                'title' => array(
                    'title' => __('Title:', 'electrapay'),
                    'type'=> 'text',
                    'description' => __('This controls the title which the user sees during checkout.', 'electrapay'),
                    'default' => __('ElectraPay', 'electrapay')),
                'description' => array(
                    'title' => __('Description:', 'electrapay'),
                    'type' => 'textarea',
                    'description' => __('This controls the description which the user sees during checkout.', 'electrapay'),
                    'default' => __('Pay securely by ElectraPay.', 'electrapay')),
                'apibaseurl' => array(
                    'title' => __('API Base URL', 'electrapay'),
                    'default'     => 'https://api.electrapayonline.com',
                    'type' => 'text',
                    'description' => __('Enter API Base url found in your ElectraPay Account')),
                'accountid' => array(
                    'title' => __('Account ID', 'electrapay'),
                    'type' => 'text',
                    'description' => __('Enter Account ID found in your ElectraPay Account')),
                'apikey' => array(
                    'title' => __('API Key', 'electrapay'),
                    'type' => 'text',
                    'description' => __('Enter API Key generated in your account on the ElectraPay website')),
                'statusupdate' => array(
                    'title' => __('Payment Status Update', 'electrapay'),
                    'type' => 'select',
                    'default'     => 'C',
                    'description' => 'Callback (most common) - ElectraPay will detect when customer pays and calls WordPress to detect changes <br>Query - WordPress sends request every 10 seconds for an update to transaction details',
                    'options'     => $pstlist),
            );
        }
        /**
         * Admin Panel Options
         * - Options for bits like 'title' and availability on a country-by-country basis
         **/
        public function admin_options(){
            echo '<h3>'.__('ElectraPay Payment Gateway', 'electrapay').'</h3>';
            echo '<p>'.__('ElectraPay is a popular payment gateway').'</p>';
            echo '<table class="form-table">';
            $this -> generate_settings_html();
            echo '</table>';

        }


        function payment_fields(){
            if($this -> description) echo wpautop(wptexturize($this -> description));
        }

        /**
         * Receipt Page
         **/
        function receipt_page_electrapay($order){
            echo $this -> generate_electrapay_form($order);
        }

        public function generate_electrapay_form($order_id){
            global $woocommerce;
            $order = new WC_Order($order_id);
            $amount = $order->order_total;
            $redirect_url = $this->get_return_url( $order );
            $orderurl = $this->apibaseurl.'/order';
            $postdata = array();
            $postdata['accountid'] = (int) $this->accountid;
            $postdata['shortdescription'] = 'Payment for Order No :'.$order_id;
            $postdata['longdescription'] = 'Payment for Order No :'.$order_id;
            $postdata['reference'] = (string)$order_id;
            $postdata['paymentcategory'] = $this->paymentcategory;
            $postdata['ordercurrency'] = $this->elcurrency;
            $postdata['orderamount'] = $amount;
            $json_request =json_encode($postdata, true);
            $returnmessage="";

            $tax = 0;
            $ShippingCost = 0;

            $response = $this->doPost($json_request, $orderurl);

            if ($response['responsecode'] != '00') {
                echo '<p> <b>Error:</b> '.$response['responsecode'].' - '.$response['responsedescription'].'</p>';
                exit;
            }

            $uuid = $response['uuid'];
            $ecaamt = $response['quoteamount'];
            update_post_meta($order_id, '_electrapay_eca_amt', $ecaamt);
            update_post_meta($order_id, '_electrapay_eca_ccy', $response['quotecurrency']);

            $returnmessage = '<p><b>Do not close this page until ElectraPay has confirmed the requested amount of confirmations after payment has beeen sent.</b></p><p>Your order has been received by ElectraPay and requires payment. Please use the qrcode below to the transaction:</p>';

$ecamainlogo = "";

	    $imagedataqt = "";


            return '<p>'.__($returnmessage, 'electrapay').'</p>'.'<img src="data:image/png;base64,'.$ecamainlogo.'" alt="The Electra Project" width="25%" /><div class="electrapayqrcode" id="electrapayqrcode"><img src="data:image/png;base64,'.$response['qrcode'].'" alt="Pay with QR Code" />
			<p><b>Wallet Address : </b>'.$response['walletaddress'].' </p>
			<p><b>Amount : </b>'.$response['quoteamount'].' '.$response['quotecurrency'].' </p>
			<p><b>Please use the EXACT amount quoted in a single payment </b></p>
			<p><b>Computer User :</b> Click button below to open in Electra wallet with information prefilled.<br>
			<b>Mobile User :</b> Click button below to open Electra Mobile Wallet and scan QR Code with Send option.</p> <a href="electra:'.$response['walletaddress'].'?amount='.$response['quoteamount'].'&label=ElectraPay%20'.$this -> accountid.'%20%23'.$order_id.'"><img src="data:image/png;base64,'.$imagedataqt.'" alt="Pay with Electra QT Wallet" /></a>
			</div>
			<script type="text/javascript">
			setInterval(function()
                   {
	                var ajaxurl = "'.admin_url('admin-ajax.php').'";
	                var data = {
			            action       : "electrapay_status",
			            uuid         : "'.$uuid.'",
			            order_id     : "'.$order_id.'",
		            }
                    jQuery.ajax({
                        type: "POST",
                        data: data,
                        url: ajaxurl,
                        success:function(response)
                            {
                                $response_json = jQuery.parseJSON(response);
                                if($response_json.status == "Yes") {
                                    window.location.href = "'.$redirect_url.'";
                                }
                            }
                        });
                     }, 10000)
            </script>';
        }


        /**
         * Process the payment and return the result
         **/
        function process_payment($order_id){
            $order = new WC_Order($order_id);
            if ($order->get_status() == 'failed') {
                $order->update_status('pending');
            }
            return array('result' => 'success', 'redirect' => $order->get_checkout_payment_url( true )
            );
        }

        public function doPost($json_request, $url)
        {
            $ch = curl_init();
            curl_setopt($ch, CURLOPT_URL, $url);
            $authcode = base64_encode($this -> accountid .':'.$this -> apikey);
            $headers = array();
            $headers[] = 'Content-type: text/xml';
            $headers[] = 'Authorization: Basic '.$authcode;
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
            curl_setopt($ch, CURLOPT_FAILONERROR, 1);
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);

            if ($json_request != '') {
                curl_setopt($ch, CURLOPT_POST, 1);
                curl_setopt($ch, CURLOPT_POSTFIELDS, $json_request);
            }
            curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);
            curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);
            $data = curl_exec($ch);

            if (curl_error($ch)) {
                $error_msg = curl_error($ch);
                curl_close($ch);
                unset($ch);
                return array('responsedescription'=>$error_msg,'responsecode'=>'ERPG500');
            }
            curl_close($ch);
            unset($ch);
            $response = json_decode($data, true);
            return $response;
        }

        //call back
        function check_electrapay_response(){
            global $woocommerce, $wpdb;

            if($_GET['wc-api']== 'wc_electrapay'){

                $jsonres = file_get_contents("php://input");
                if ($jsonres) {
                    $respar = json_decode($jsonres, true);
                }
                else  {
                    exit;
                }

                $order = new WC_Order($respar['reference']);
                $error_msg = '';
                $error_msgurl = '';

                if (strtoupper($respar['orderstatus']) == 'PAYMENT_RECEIVED') {

                    $ecaamt = get_post_meta($order->ID , '_electrapay_eca_amt',true);
                    $ccy = get_post_meta($order->ID , '_electrapay_eca_ccy',true);
                    $transaction_id =  $respar['orderreceivetransaction'];
                    $uuid = $respar['uuid'];
                    update_post_meta($order->ID, '_electrapay_uuid', $uuid);
                    $order -> payment_complete();
                    $order -> add_order_note('ElectraPay Payment Successful<br/>ElectraPay Ref: '.$uuid.', Trid :'.$transaction_id.' Amount :'.$ecaamt.' '.$ccy);
                    $woocommerce -> cart -> empty_cart();
                    $redirect_url = $this->get_return_url( $order );
                    update_post_meta($order->ID, '_electrapay_uuid', $respar['uuid']);
                }
                else {
                    if (strtoupper($respar['orderstatus']) != 'AWAITING_PAYMENT') {
                        $order->update_status('failed', 'Failed, Payment Status : '.$respar['orderstatus']);
                    }
                    else {
                        $error_msg = $respar['responsedescription'];
                        $order -> add_order_note('Failed, Error Message '.$error_msg);
                        echo "err";
                    }
                }

                wp_redirect( $redirect_url );
                exit;
            }
        }
    }

    /**
     * Add the Gateway to WooCommerce
     **/
    function woocommerce_add_electrapay_gateway($methods) {
        $methods[] = 'WC_Electrapay';
        return $methods;
    }

    add_filter('woocommerce_payment_gateways', 'woocommerce_add_electrapay_gateway' );

}




add_action('wp_ajax_nopriv_electrapay_status', 'electrapay_status'  );
add_action('wp_ajax_electrapay_status', 'electrapay_status'  );

function electrapay_status() {
    global $woocommerce;

    $responsear['status'] = 'No';
    $Electrapay = new WC_Electrapay();


    if ($Electrapay->statusupdate == 'Q') {

        $url = $Electrapay->apibaseurl.'/order/'.$_REQUEST['uuid'];

        $response = $Electrapay->doPost('', $url);


        if ($response['responsecode'] == 'ERPG500') $response['responsecode'] = '00';

        if ($response['responsecode'] != '00') {
            $msg['class'] = 'error';
            $woocommerce -> cart -> empty_cart();
            $msg['message'] = "transaction has been declined. - ". $response['responsecode'].' - '.$response['responsedescription'];
            if (function_exists('wc_add_notice')) {
                wc_add_notice($msg['message'], $msg['class']);
            }
            $responsear['status'] = 'Yes';
            echo json_encode($responsear, true);
            exit;
        }

        if  (strtoupper($response['orderstatus']) == 'PAYMENT_RECEIVED') {
            $order_id = $response['reference'] ;
            update_post_meta($order_id, '_electrapay_uuid', $_REQUEST['uuid']);
            $ecaamt = get_post_meta($order_id , '_electrapay_eca_amt',true);
            $ccy = get_post_meta($order_id , '_electrapay_eca_ccy',true);
            $uuid =  $response['uuid'];
            $transaction_id =  $response['orderreceivetransaction'];
            $responsear['status'] = 'Yes';
            $order = wc_get_order( $order_id );
            $order -> payment_complete();
            $woocommerce -> cart -> empty_cart();
            $order -> add_order_note('ElectraPay Payment Successful<br/>ElectraPay Ref: '.$uuid.', Trid :'.$transaction_id.' Amount :'.$ecaamt.' '.$ccy);
        }
        if ((strtoupper($response['orderstatus']) != 'AWAITING_PAYMENT') && (strtoupper($response['orderstatus']) != 'PAYMENT_RECEIVED') && (strtoupper($response['orderstatus']) != 'PAYMENT_REJECTED') && (strtoupper($response['orderstatus']) != 'NEW')) {
            $order_id = $response['reference'] ;
            $order = wc_get_order( $order_id );
            $order->update_status('failed', $response['orderstatus']);
            $responsear['status'] = 'Yes';
        }
    }
    else {

        $order = wc_get_order( $_REQUEST['order_id'] );
        $order_status = $order->get_status();

        if ($order_status != 'pending') {
            $responsear['status'] = 'Yes';
        }
        else {
            $uuid = get_post_meta($_REQUEST['order_id'], '_electrapay_uuid', true);
            if (!empty($uuid)) $responsear['status'] = 'Yes';
        }
    }

    echo json_encode($responsear, true);
    exit;
}



function electrapay_order_status_refunded( $order_id,$old_status,$new_status ){
    global $woocommerce;
    if ($new_status == 'refunded') {

        $uuid = get_post_meta($order_id , '_electrapay_uuid',true);
        $new_status = $old_status;


        if (!empty($uuid)) {

            $Electrapay = new WC_Electrapay();
            $url = $Electrapay->apibaseurl.'/order/'.$uuid.'/reverse';

            $postdata['uuid'] = $uuid;
            $json_request =json_encode($postdata, true);
            $response = $Electrapay->doPost($json_request, $url);
            $order = wc_get_order( $order_id );

            if ($response['responsecode'] != '00') {
                $errmsg = 'Unable to reverse :'. $response['responsecode'].' - '.$response['responsedescription'];
                $order->update_status($old_status, $errmsg);
                throw new \Exception($errmsg);

            }
            else {
                $order -> add_order_note('ElectraPay Payment Reverse Successful');
                update_post_meta($order_id, '_electrapay_uuid', '');
                update_post_meta($order_id, '_electrapay_reverse', $uuid);
            }
        }
    }
}
add_action( 'woocommerce_order_status_changed', 'electrapay_order_status_refunded', 10, 3 );
